import { useSelector, useDispatch } from 'react-redux';
import { SET_USER_PRICE } from '../redux/actions';

function Main() {

const {price, nameOfService} = useSelector(state => state.service)
console.log(price, nameOfService)

const dispatch = useDispatch();

const setUserPriceHandler = (e)=>{
    dispatch({
        type: SET_USER_PRICE,
        payload: e.target
    })

}

  return (
    <div className="App">
      <form>
        <input type='text' value={nameOfService} required/>
        <input type='number' onChange={setUserPriceHandler} value={price} required/>
        <button>Save</button>
        <button>Cancel</button>
      </form>
      <ul>
        {}
      </ul>     
    </div>
  )
}

export default Main;
