import Form from './Form';
import { useSelector } from 'react-redux';
import { 
    SET_USER_PRICE,
    SET_USER_SERVICE_NAME,
    ADD_ITEM_TO_LIST
 } from '../store/actions';

function Main() { 
    const { list } = useSelector(state => state.list)
  return (
    <div className="App">
    <Form />
    <div>
        {list.map(listItem=>
            <div style={{display:'flex'}}>
                <div key={new Date().getTime()}>Услуга: {listItem.nameOfService}, цена: {listItem.price}</div>
                <button>Edit</button>
                <button>&times;</button>
            </div>
        )}

      </div>     
    </div>
  )
}

export default Main;
