import { useSelector, useDispatch } from 'react-redux';
import { 
    SET_USER_PRICE,
    SET_USER_SERVICE_NAME,
    ADD_ITEM_TO_LIST,
    SET_USER_CANCEL
 } from '../store/actions';

function Form() {
    const { price, nameOfService, cancel } = useSelector(state => state.service)    
    const dispatch = useDispatch();    

    const setUserPriceHandler = (e)=>{
        dispatch({
            type: SET_USER_PRICE,
            payload: e.target.value
        })
    }

    const setUserServiceNameHandler = (e)=>{
        dispatch({
            type: SET_USER_SERVICE_NAME,
            payload: e.target.value
        })
    }

    const onSaveDataHandler =(e)=>{ 
        e.preventDefault()

        dispatch({
            type: ADD_ITEM_TO_LIST,
            payload: {
                nameOfService,
                price
            }
        })

        dispatch({
            type: SET_USER_SERVICE_NAME,
            payload: ''
        })

        dispatch({
            type: SET_USER_PRICE,
            payload: 0
        })
    }

    const onCancelHandler = (e) => {
        e.preventDefault()

        dispatch({
            type: SET_USER_SERVICE_NAME,
            payload: ''
        })

        dispatch({
            type: SET_USER_PRICE,
            payload: 0
        })

        dispatch({
            type: SET_USER_CANCEL,
            payload: false
        })           
    }

  return (
    <form>
        <input type='text' onChange={setUserServiceNameHandler} value={nameOfService} required/>
        <input type='number' onChange={setUserPriceHandler} value={price} required/>
        {!cancel && <button onClick={onSaveDataHandler}>Save</button>}
        {cancel && <button onClick={onEditDataHandler}>Save</button>}
        {cancel && <button onClick={onCancelHandler}>Cancel</button>}
  </form>
  )
}

export default Form;
