import { SET_USER_PRICE, SET_USER_SERVICE_NAME, SET_USER_CANCEL } from "../actions"

const initialState = {
    nameOfService: '',
    price: 0,
    cancel: false
}

function serviceReducer(state=initialState, action) {
    switch(action.type){
        case SET_USER_PRICE:
            return {
                ...state,
                price: action.payload
            }

        case SET_USER_SERVICE_NAME:
            return {
                ...state,
                nameOfService: action.payload             
            }
        case SET_USER_CANCEL:
            return {
                ...state,
            }

        default:
            return state
    }  
}

export default serviceReducer;
