import { ADD_ITEM_TO_LIST } from "../actions";

const initialState = []

function listReducer(state=initialState, action) {
    switch(action.type){
        case ADD_ITEM_TO_LIST:
            return [...state, action.payload]
        default:
            return state
    }  
}

export default listReducer;
