import { ADD_ITEM_TO_LIST, EDIT_ITEM_LIST, DELETE_ITEM_LIST } from "../actions";

const initialState = {
    list:[]
}

function listReducer(state=initialState, action) {
    switch(action.type){
        case ADD_ITEM_TO_LIST:
            return {...state, list: [...state.list, action.payload]}

        case EDIT_ITEM_LIST:
            return {...state, list: [...state.list].filter(listItem => {if(listItem.id === action.payload.id){listItem = action.payload}}  )}
        case DELETE_ITEM_LIST:
            return {...state, list: [...state.list].filter(listItem => listItem.id !== action.payload)}
        default:
            return state
    }  
}

export default listReducer;


