import { useDispatch } from "react-redux"
import { SET_USER_CANCEL, SET_USER_PRICE, SET_USER_SERVICE_NAME, DELETE_ITEM_LIST } from "../store/actions";


export default function ListItem(props) {
    const dispatch = useDispatch();

    const onEditHandler =()=>{
        dispatch({
            type: SET_USER_SERVICE_NAME,
            payload: props.nameOfService
        })

        dispatch({
            type: SET_USER_PRICE,
            payload: props.price
        })   

        dispatch({
            type: SET_USER_CANCEL,
            payload: true
        })
    }

    const onDeleteHandler =()=>{
        dispatch({
            type: DELETE_ITEM_LIST,
            payload: props.id
        })
    }



  return (
    <div style={{display:'flex'}}>
        <div>Услуга: {props.nameOfService}, цена: {props.price}</div>
        <button onClick={onEditHandler}>Edit</button>
        <button onClick={onDeleteHandler}>&times;</button>
    </div>
  )
}
