import { useSelector, useDispatch } from 'react-redux';
import { 
    SET_USER_PRICE,
    SET_USER_SERVICE_NAME,
    ADD_ITEM_TO_LIST
 } from '../store/actions';

function Main() {

    const {price, nameOfService} = useSelector(state => state.service)
    const list = useSelector(state => state.list)
    const dispatch = useDispatch();

    const setUserPriceHandler = (e)=>{
        dispatch({
            type: SET_USER_PRICE,
            payload: e.target.value
        })
    }

    const setUserServiceNameHandler = (e)=>{
        dispatch({
            type: SET_USER_SERVICE_NAME,
            payload: e.target.value
        })
    }

    const onSaveData =(e)=>{ 
        e.preventDefault()

        dispatch({
            type: ADD_ITEM_TO_LIST,
            payload: {
                nameOfService,
                price
            }
        })

        dispatch({
            type: SET_USER_SERVICE_NAME,
            payload: ''
        },

        )

        dispatch({
            type: SET_USER_PRICE,
            payload: 0
        })
    }    

  return (
    <div className="App">
      <form>
        <input type='text' onChange={setUserServiceNameHandler} value={nameOfService} required/>
        <input type='number' onChange={setUserPriceHandler} value={price} required/>
        <button onClick={onSaveData}>Save</button>
        <button>Cancel</button>
      </form>
      <ul>
        {list.map(listItem=><li>Услуга: {listItem.nameOfService}, цена: {listItem.price}</li>)}
      </ul>     
    </div>
  )
}

export default Main;
