import { 
    SET_USER_PRICE,
    SET_USER_SERVICE_NAME,
    ADD_ITEM_TO_LIST
 } from '../store/actions';

function Main() {

    const { price, nameOfService } = useSelector(state => state.service)
    const { list } = useSelector(state => state.list)
    const dispatch = useDispatch();

    const setUserPriceHandler = (e)=>{
        dispatch({
            type: SET_USER_PRICE,
            payload: e.target.value
        })
    }

    const setUserServiceNameHandler = (e)=>{
        dispatch({
            type: SET_USER_SERVICE_NAME,
            payload: e.target.value
        })
    }

    const onSaveData =(e)=>{ 
        e.preventDefault()

        dispatch({
            type: ADD_ITEM_TO_LIST,
            payload: {
                nameOfService,
                price
            }
        })

        dispatch({
            type: SET_USER_SERVICE_NAME,
            payload: ''
        })

        dispatch({
            type: SET_USER_PRICE,
            payload: 0
        })
    }    

  return (
    <div className="App">


      <div>
        {list.map(listItem=>
            <div style={{display:'flex'}}>
                <div key={new Date().getTime()}>Услуга: {listItem.nameOfService}, цена: {listItem.price}</div>
                <button>Edit</button>
                <button>&times;</button>
            </div>
        )}

      </div>     
    </div>
  )
}

export default Main;
