import { useState, useEffect } from 'react';
import { useSelector } from 'react-redux';
import ListItem from './ListItem';
import Filter from './Filter';

export default function List() {
  const { list } = useSelector(state => state.list) 
  const { filterValue } = useSelector(state => state.filter)

  const [filtredList, setFiltredList] = useState(list)   

  useEffect(()=>{
    setFiltredList(prev => prev.filter((listItem => filterValue === listItem.nameOfService)))    
  }, [filterValue]);



  return (
    <>
        <Filter />
        {filtredList.map(listItem => <ListItem key={listItem.id} nameOfService={listItem.nameOfService} price={listItem.price} id={listItem.id} />)}
    </>


  )
}
